import DashboardCard from "./DashboardCard";
import { CircularProgressbar, buildStyles } from "react-circular-progressbar";
import "react-circular-progressbar/dist/styles.css";

import "../styles/containerdashboardcard.css";

const ContainerDashboardCard = () => {
  const percentage = 60;
  return (
    <>
      <div id="containerDashboardCard">
        <DashboardCard style={{ gridColumn: "1 / 4", gridRow: "1 / 4" }}>
          {/* <h2>Dernier objectif</h2> */}
          <div className="flexContainer">
            <div id="containerCardRight">
              <h2>Apprendre l'anglais</h2>
              <ul id="ulDashboardCard">
                <li className="liDashboardCard">
                  Dernière tâche : Apprendre le preterit present
                </li>
                <li className="liDashboardCard">
                  Prochaine tâche : Revoir les verbes irréguliers
                </li>
                <li className="liDashboardCard">Status : En cours</li>
                <li className="liDashboardCard">Tâches : 10/23</li>
                <li className="liDashboardCard">Difficulté : Intermédiaire</li>
              </ul>
            </div>
            <div
              className="ContainerCircularProgressBar"
              style={{ width: 200, height: 200 }}
            >
              <CircularProgressbar
                value={percentage}
                text={`${percentage}%`}
                strokeWidth={18}
                styles={buildStyles({
                  rotation: 0.25,
                  strokeLinecap: "butt",
                  textSize: "15px",
                  pathTransitionDuration: 0.5,
                  textColor: "white",
                  trailColor: "white",
                  pathColor: "#103164",
                })}
              />
            </div>
          </div>
        </DashboardCard>
        <DashboardCard style={{ gridColumn: "4 / 7", gridRow: "1 / 4" }}>
          <h2>Programme de la journée</h2>
          <select>
            <option value="none">Choissisez votre objectif</option>
            <option value="apprendreAnglais">Apprendre l'anglais</option>
            <option value="apprendrePiano">Apprendre le piano</option>
            <option value="OneGoal">Développer One Goal</option>
          </select>
          <select>
            <option value="none">Choissisez votre sous-objectif</option>
            <option value="apprendreAnglais">Apprendre l'anglais</option>
            <option value="apprendrePiano">Apprendre le piano</option>
            <option value="OneGoal">Développer One Goal</option>
          </select>
        </DashboardCard>
        <DashboardCard
          style={{ gridColumn: "1 / 7", gridRow: "4 / 7" }}
        ></DashboardCard>
        <DashboardCard
          style={{ gridColumn: "1 / 4", gridRow: "7 / 10" }}
        ></DashboardCard>
        <DashboardCard
          style={{ gridColumn: "4 / 7", gridRow: "7 / 10" }}
        ></DashboardCard>
      </div>
    </>
  );
};
export default ContainerDashboardCard;
