import Menu from "../components/Menu";
import Navbar from "../components/Navbar";
import DashboardCard from "../components/DashboardCard";

import { VictoryBar, VictoryChart, VictoryAxis } from "victory";

import "../styles/progression.css";

const Progression = () => {
  const StyledBar = styled(Bar)`
    fill: purple;
  `;

  const StyledLabel = styled(VictoryLabel)`
    tspan {
      fill: magenta;
      font-family: Papyrus, fantasy;
    }
  `;

  const data = [
    { years: "2020", goals: "3" },
    { years: "2021", goals: "2" },
    { years: "2022", goals: "4" },
    { years: "2023", goals: "2" },
  ];
  return (
    <>
      <div className="gridContainer">
        <Menu />
        <Navbar titlePage="PROGRESSION" />
        <div id="containerDashboardCard">
          <DashboardCard style={{ gridColumn: "1 / 4", gridRow: "1 / 4" }}>
            <h2>Progression</h2>
            <ul id="ulDashboardCard">
              <li className="liDashboardCard">Niveau actuel : Entrepreneur</li>
              <li className="liDashboardCard">Score : 1809</li>
              <li className="liDashboardCard">Objectif réalisé : 18</li>
              <li className="liDashboardCard">Tâches réalisés : 125</li>
            </ul>
          </DashboardCard>
          <DashboardCard
            style={{ gridColumn: "4 / 7", gridRow: "1 / 4" }}
          ></DashboardCard>
          <DashboardCard
            style={{
              gridColumn: "1 / 7",
              gridRow: "4 / 9",
              backgroundColor: "white",
            }}
          >
            <h2 id="titreChart">Objectif réalisés</h2>
            <VictoryChart domainPadding={20}>
              <VictoryBar
                labels={[1, 2, 3, 4]}
                dataComponent={<StyledBar />}
                labelComponent={<StyledLabel />}
                data={data}
                // data accessor for x values
                x="years"
                // data accessor for y values
                y="goals"
              />
            </VictoryChart>
          </DashboardCard>
        </div>
      </div>
    </>
  );
};
export default Progression;
