import Menu from "../components/Menu";
import Navbar from "../components/Navbar";
import DashboardCard from "../components/DashboardCard";
import React, { FunctionComponent } from "react";
import { LineChart, Line, CartesianGrid, XAxis, YAxis } from "recharts";

import "../styles/progression.css";

const Progression = () => {
  const data = [
    { name: "", uv: 0, pv: 100, amt: 100 },
    { name: "2022", uv: 2, pv: 100, amt: 100 },
    { name: "2023", uv: 6, pv: 100, amt: 100 },
    { name: "2024", uv: 8, pv: 100, amt: 100 },
    { name: "2025", uv: "none", pv: 100, amt: 100 },
    { name: "2026", uv: "none", pv: 100, amt: 100 },
  ];

  // const CustomizedAxisTick: FunctionComponent<any> = (props: any) => {
  //   const { x, y, payload } = props;
  const CustomizedAxisTick = () => {
    return (
      <g transform={`translate(${x},${y})`}>
        <text
          x={-40}
          y={0}
          dy={16}
          textAnchor="end"
          fill="red"
          transform="rotate(-60)"
        ></text>
      </g>
    );
  };

  return (
    <>
      <div className="gridContainer">
        <Menu />
        <Navbar titlePage="PROGRESSION" />
        <div id="containerDashboardCard">
          <DashboardCard style={{ gridColumn: "1 / 4", gridRow: "1 / 4" }}>
            <h2>Progression</h2>
            <ul id="ulDashboardCard">
              <li className="liDashboardCard">Niveau actuel : Entrepreneur</li>
              <li className="liDashboardCard">Score : 1809</li>
              <li className="liDashboardCard">Objectif réalisé : 18</li>
              <li className="liDashboardCard">Tâches réalisés : 125</li>
            </ul>
          </DashboardCard>
          <DashboardCard
            style={{ gridColumn: "4 / 7", gridRow: "1 / 4" }}
          ></DashboardCard>
          <DashboardCard
            style={{
              gridColumn: "1 / 7",
              gridRow: "4 / 9",
              backgroundColor: "white",
            }}
          >
            <h2 id="titreChart">Objectif réalisés</h2>
            <div id="alignChartCenter">
              <LineChart
                id="chart"
                width={1000}
                height={350}
                data={data}
                color="#0d5ed7"
                margin={{
                  top: 10,
                  right: 35,
                  left: 0,
                  bottom: 0,
                }}
              >
                <Line dataKey="uv" stroke="#0d5ed7" strokeWidth={3} />
                <CartesianGrid stroke="none" />
                <XAxis
                  dataKey="name"
                  stroke="#0d5ed7"
                  strokeWidth={3}
                  tick={<CustomizedAxisTick />}
                />
                <YAxis stroke="#0d5ed7" strokeWidth={3} />
              </LineChart>
            </div>
          </DashboardCard>
        </div>
      </div>
    </>
  );
};
export default Progression;
