import DashboardCard from "./DashboardCard";
import { CircularProgressbar, buildStyles } from "react-circular-progressbar";
import "react-circular-progressbar/dist/styles.css";

import "../styles/containerdashboardcard.css";

const ContainerDashboardCard = () => {
  const percentage = 30;
  return (
    <>
      <div id="containerDashboardCard">
        <DashboardCard style={{ gridColumn: "1 / 4", gridRow: "1 / 4" }}>
          <div className="flexContainer">
            <div id="containerCardRight"></div>
            <h2>Apprendre l'anglais</h2>
            <ul>
              <li>Dernière tâche : Apprendre le preterit present</li>
              <li>Prochaine tâche : Revoir les verbes irréguliers</li>
              <li>Status : En cours</li>
              <li>Tâches : 10/23</li>
              <li>Difficulté : Intermédiaire</li>
            </ul>
            <div
              className="ContainerCircularProgressBar"
              style={{ width: 100, height: 100 }}
            >
              <CircularProgressbar
                value={percentage}
                text={`${percentage}%`}
                strokeWidth={18}
                styles={buildStyles({
                  rotation: 0.25,
                  strokeLinecap: "butt",
                  textSize: "15px",
                  pathTransitionDuration: 0.5,
                  textColor: "white",
                  trailColor: "white",
                  pathColor: "#103164",
                })}
              />
            </div>
          </div>
        </DashboardCard>
      </div>
    </>
  );
};
export default ContainerDashboardCard;
