import Menu from "../components/Menu";
import Navbar from "../components/Navbar";
import DashboardCard from "../components/DashboardCard";
import ProgressBar from "@ramonak/react-progress-bar";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import { Bar } from "react-chartjs-2";

import "../styles/progression.css";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);
const Progression = () => {
  const options = {
    responsive: true,
    plugins: {
      title: {
        display: true,
        text: "Chart.js Bar Chart",
      },
    },
  };

  const labels = [
    "January",
    "February",
    "March",
    "April",
    "May",
    "June",
    "July",
  ];
  const data = {
    labels,
    datasets: [
      {
        label: "Dataset 1",
        data: labels.map(() => faker.datatype.number({ min: 0, max: 1000 })),
        backgroundColor: "rgba(255, 99, 132, 0.5)",
      },
      {
        label: "Dataset 2",
        data: labels.map(() => faker.datatype.number({ min: 0, max: 1000 })),
        backgroundColor: "rgba(53, 162, 235, 0.5)",
      },
    ],
  };
  const dataGoals = {
    // Les étiquettes sur l'axe des x
    labels: ["2020", "2021", "2022", "2023", "2024", "2025"],

    // Tableau contenant vos ensembles de données
    datasets: [
      {
        HoverBackgroundColor: "#03265a",
        // Légende pour cet ensemble de données
        label: "Objectifs réalisés durant l'année",

        // Les données/ valeurs de l'ensemble de données
        data: [2, 4, 3, 2],

        // Couleur de fond pour chaque barre
        backgroundColor: "#0d5ed7",

        // Couleur de la bordure pour chaque barre
        borderColor: "#0d5ed7",

        // Épaisseur de la bordure pour chaque barre
        borderWidth: 1,
      },
      // Vous pouvez ajouter plus d'ensembles de données ici si nécessaire
    ],
  };

  const dataSubGoals = {
    // Les étiquettes sur l'axe des x
    labels: ["Janvier", "Février", "Mars", "Avril", "Mai", "Juin"],

    // Tableau contenant vos ensembles de données
    datasets: [
      {
        HoverBackgroundColor: "#03265a",
        // Légende pour cet ensemble de données
        label: "Sous-Objectifs réalisés durant le mois",

        // Les données/ valeurs de l'ensemble de données
        data: [20, 42, 34, 9, 12, 31],

        // Couleur de fond pour chaque barre
        backgroundColor: "#0d5ed7",

        // Couleur de la bordure pour chaque barre
        borderColor: "#0d5ed7",

        // Épaisseur de la bordure pour chaque barre
        borderWidth: 1,
      },
      // Vous pouvez ajouter plus d'ensembles de données ici si nécessaire
    ],
  };

  return (
    <>
      <div className="gridContainer">
        <Menu />
        <Navbar titlePage="PROGRESSION" />
        <div id="containerDashboardCard">
          <DashboardCard style={{ gridColumn: "1 / 4", gridRow: "1 / 4" }}>
            <h2>Progression</h2>
            <ul id="ulDashboardCard">
              <li className="liDashboardCard">Niveau actuel : Entrepreneur</li>
              <li className="liDashboardCard">Score : 1809</li>
              <li className="liDashboardCard">Objectif réalisé : 18</li>
              <li className="liDashboardCard">Tâches réalisés : 125</li>
            </ul>
          </DashboardCard>
          <DashboardCard style={{ gridColumn: "4 / 7", gridRow: "1 / 4" }}>
            <h2>Status</h2>
            <div id="containerProgressBar">
              <div id="containerLevelProgressBar">
                <p>Entrepreneur</p>
                <p>PDG</p>
              </div>
              <ProgressBar
                completed={80}
                maxCompleted={100}
                className="wrapper"
                barContainerClassName="container"
                completedClassName="barCompleted"
                labelClassName="label"
              />
              <p>Plus que 5 objectifs à réaliser</p>
            </div>
          </DashboardCard>
          <DashboardCard
            style={{
              gridColumn: "1 / 7",
              gridRow: "4 / 9",
              backgroundColor: "white",
            }}
          >
            <div id="containerPresentationChart">
              <div id="containerChartLeft">
                <h2 id="titreChart">Objectif réalisés</h2>
                <div class="containerChart">
                  <Bar options={options} data={data} />;
                </div>
              </div>
              <div id="containerChartRight">
                <h2 id="titreChart">Sous-objectif réalisés</h2>
                <div class="containerChart"></div>
              </div>
            </div>
          </DashboardCard>
        </div>
      </div>
    </>
  );
};
export default Progression;
