import Menu from "../components/Menu";
import Navbar from "../components/Navbar";
import DashboardCard from "../components/DashboardCard";
import { Bar } from "react-chartjs-2";
import Chart from "chart.js/auto";

import "../styles/progression.css";

const Progression = () => {
  const dataGoals = {
    // Les étiquettes sur l'axe des x
    labels: ["2020", "2021", "2022", "2023", "2024", "2025"],

    // Tableau contenant vos ensembles de données
    datasets: [
      {
        HoverBackgroundColor: "#03265a",
        // Légende pour cet ensemble de données
        label: "Objectifs réalisés durant l'année",

        // Les données/ valeurs de l'ensemble de données
        data: [2, 4, 3, 2],

        // Couleur de fond pour chaque barre
        backgroundColor: "#0d5ed7",

        // Couleur de la bordure pour chaque barre
        borderColor: "#0d5ed7",

        // Épaisseur de la bordure pour chaque barre
        borderWidth: 1,
      },
      // Vous pouvez ajouter plus d'ensembles de données ici si nécessaire
    ],
  };

  const dataSubGoals = {
    // Les étiquettes sur l'axe des x
    labels: ["2020", "2021", "2022", "2023", "2024", "2025"],

    // Tableau contenant vos ensembles de données
    datasets: [
      {
        HoverBackgroundColor: "#03265a",
        // Légende pour cet ensemble de données
        label: "Objectifs réalisés durant l'année",

        // Les données/ valeurs de l'ensemble de données
        data: [2, 4, 3, 2],

        // Couleur de fond pour chaque barre
        backgroundColor: "#0d5ed7",

        // Couleur de la bordure pour chaque barre
        borderColor: "#0d5ed7",

        // Épaisseur de la bordure pour chaque barre
        borderWidth: 1,
      },
      // Vous pouvez ajouter plus d'ensembles de données ici si nécessaire
    ],
  };

  return (
    <>
      <div className="gridContainer">
        <Menu />
        <Navbar titlePage="PROGRESSION" />
        <div id="containerDashboardCard">
          <DashboardCard style={{ gridColumn: "1 / 4", gridRow: "1 / 4" }}>
            <h2>Progression</h2>
            <ul id="ulDashboardCard">
              <li className="liDashboardCard">Niveau actuel : Entrepreneur</li>
              <li className="liDashboardCard">Score : 1809</li>
              <li className="liDashboardCard">Objectif réalisé : 18</li>
              <li className="liDashboardCard">Tâches réalisés : 125</li>
            </ul>
          </DashboardCard>
          <DashboardCard
            style={{ gridColumn: "4 / 7", gridRow: "1 / 4" }}
          ></DashboardCard>
          <DashboardCard
            style={{
              gridColumn: "1 / 7",
              gridRow: "4 / 9",
              backgroundColor: "white",
            }}
          >
            <div id="containerPresentationChart">
              <div id="containerChartLeft">
                <h2 id="titreChart">Objectif réalisés</h2>
                <div class="containerChart">
                  <Bar data={dataGoals} />
                </div>
              </div>
              <div id="containerChartRight">
                <h2 id="titreChart">Sous-objectif réalisés</h2>
                <div class="containerChart">
                  <Bar data={dataSubGoals} />
                </div>
              </div>
            </div>
          </DashboardCard>
        </div>
      </div>
    </>
  );
};
export default Progression;
