import Menu from "../components/Menu";
import DashboardCard from "../components/DashboardCard";
import Navbar from "../components/Navbar";
import "../styles/message.css";
const Message = () => {
  return (
    <>
      <div className="gridContainer">
        <Menu />
        <Navbar titlePage="NOUVEAU MESSAGE" />
        <div id="containerDashboardCard">
          <DashboardCard
            style={{
              backgroundColor: "white",
              gridColumn: "1 / 7",
              gridRow: "1 / 6",
              margin: "0px 100px",
            }}
          >
            <div id="flexContainerMessage">
              <p id="titreMessageUnique">Revoir le cours progressivement</p>
              <p id="dateMessage">08/10/23</p>
            </div>
            {/* <h2 id="categorieMessageUnique">Observation</h2> */}
          </DashboardCard>
        </div>
      </div>
    </>
  );
};
export default Message;
