import Navbar from "../components/Navbar";
import Menu from "../components/Menu";
import DashboardCard from "../components/DashboardCard";
import Btn from "../components/Btn";
import Formulaire from "../components/Formulaire";
import Input from "../components/Input";
import "../styles/profile.css";

import iconNewPhoto from "../assets/iconNewPhoto.png";

const Profile = () => {
  return (
    <>
      <div className="gridContainer">
        <Menu />
        <Navbar titlePage="PROFILE" />
        <div id="containerDashboardCard">
          <DashboardCard
            style={{
              gridColumn: "1 / 3",
              gridRow: "1 / 6",
              textAlign: "center",
            }}
          >
            <div id="firstElipse">
              <div id="secondElipse">
                <img id="iconNewPhoto" src={iconNewPhoto} />
              </div>
            </div>
            <p>Format acceptés : jpeg, png, pdf</p>
            <p>Taille maximale : 6 mo</p>
          </DashboardCard>
          <DashboardCard style={{ gridColumn: "3 / 5", gridRow: "1 / 6" }}>
            <Formulaire title="Vos informations :">
              <Input title="Adresse mail :" typeInput="email" />
              <Input title="Nom :" typeInput="text" />
              <Input title="Prénom :" typeInput="text" />
              <Input title="Téléphone :" typeInput="text" />
              <Btn
                title="Modifier"
                style={{
                  backgroundColor: "white",
                  color: "#0d5ed7",
                  width: "327px",
                  margin: "10px 50px",
                }}
              />
            </Formulaire>
          </DashboardCard>
          <DashboardCard
            style={{ gridColumn: "5 / 7", gridRow: "1 / 6" }}
          ></DashboardCard>
          <Btn title="Supprimer le compte" />
        </div>
      </div>
    </>
  );
};
export default Profile;
